##########################
# COMBIEN DE JOURNALISTE #
##########################
def taille(liste)
	return liste.length ##379 #size
end

####################
# CONTIENT CHIFFRE #
####################
def contient_nb_de(liste)
	result = liste.select {|item| item.match(/\d/)}
	return result.size
end
