#################
# CONTIENT AUDE #
#################
def contient_mot(liste, mot)
	rep = liste.select{|a| a.include?(*mot)}
	return rep.size
end

#####################
# COMMENCE AVEC MAJ #
#####################
def begin_maj(liste)
	nb_pseudo=0
	maj=("A".."Z")

	liste.each do |pseudo|
		maj.each do |majuscule|
			if pseudo[1] == majuscule
				nb_pseudo += 1
			end
		end
	end
	return nb_pseudo
end
# Fonction 2 #
def begin_maj2(liste, mot)
	liste_mot=liste.select{|a| a.start_with?(*mot)}
	return liste_mot.size
end

###################
# CONTIENNENT MAJ #
###################
def have_maj(liste)
	num_pseudo = 0
	maj=("A".."Z")

	liste.each do |pseudo| #passe de pseudo en pseudo
		i=0
		while i<pseudo.length
			if maj.include?(pseudo[i])
				num_pseudo+=1
				break
			end
			i+=1
		end
	end
	return num_pseudo
end


####################
# UNDERSCORE ( _ ) #
####################
def underscore_count(liste)
	nb_underscore = liste.select{|a| a.include? "_"}
	return nb_underscore.size
end

##################################
# TRIER LISTE ORDRE ALPHABETIQUE #
##################################
def trie(liste)
	liste.sort do |persA, persB|
		persA<=>persB
	end
end

